import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) {
      alert('Please provide both an image and a question.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessages([...messages, { text: question, type: 'user' }, { text: response.data.answer, type: 'bot' }]);
      setQuestion(''); // Clear the input field
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <h4>"Where vision meets conversation—seamlessly recognizing and discussing your images."</h4>
        </div>
      </div>
      <div className="main-content">
        <div className="chat-interface">
          <h2><FaRobot className="robot-icon" /> Vision Dialog</h2>
          <p className="online-status">• Online</p>
          {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-result" />}
          <div className="chat-container">
            {messages.map((msg, index) => (
              <div key={index} className={`chat-bubble ${msg.type}-bubble`}>
                <p>{msg.text}</p>
              </div>
            ))}
          </div>
          {error && <p className="error">{error}</p>}
          <form className="input-form" onSubmit={handleSubmit}>
            <input
              type="file"
              onChange={handleImageUpload}
              className="file-input"
              accept="image/*"
            />
            <input
              type="text"
              placeholder="Ask me anything!"
              value={question}
              onChange={handleQuestionChange}
              className="text-input"
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Send'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default App;
