// === FRONTEND: src/App.jsx ===

import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [conversation, setConversation] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [imageUploaded, setImageUploaded] = useState(false);
  const [sessionId, setSessionId] = useState('');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);
    const reader = new FileReader();
    reader.onloadend = () => setImagePreviewUrl(reader.result);
    if (file) reader.readAsDataURL(file);
  };

  const handleQuestionChange = (event) => setQuestion(event.target.value);

  const handleFirstSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) return alert('Please provide both an image and a question.');

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/start_session', formData);
      if (response.data.answer && response.data.session_id) {
        setSessionId(response.data.session_id);
        setConversation([{ question, answer: response.data.answer }]);
        setQuestion('');
        setImageUploaded(true);
      } else {
        setError('No answer or session ID received.');
      }
    } catch (error) {
      console.error('Error starting session:', error);
      setError('Failed to get the answer. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFollowUp = async () => {
    if (!question || !sessionId) return;
    const formData = new FormData();
    formData.append('question', question);
    formData.append('session_id', sessionId);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask', formData);
      if (response.data.answer) {
        setConversation([...conversation, { question, answer: response.data.answer }]);
        setQuestion('');
      } else {
        setError('No answer received.');
      }
    } catch (error) {
      console.error('Error asking question:', error);
      setError('Failed to get the answer. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = async () => {
    setLoading(true);
    setError('');
    try {
      await axios.post('http://localhost:5000/reset_session');
      setImage(null);
      setImagePreviewUrl(null);
      setImageUploaded(false);
      setConversation([]);
      setQuestion('');
      setSessionId('');
    } catch (error) {
      setError('Failed to reset the session.');
      console.error('Reset error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <h4>"Where vision meets conversation—seamlessly recognizing and discussing your images."</h4>
        </div>
      </div>
      <div className="main-content">
        <div className="chat-interface">
          <h2 className='vision-dialog-text'><FaRobot className="robot-icon" /> Vision Dialog</h2>
          <p className="online-status">• Online</p>

          {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-chat" />}

          <div className="chat-container">
            {conversation.map((chat, index) => (
              <div key={index}>
                <div className="chat-bubble user-bubble"><p>{chat.question}</p></div>
                <div className="chat-bubble bot-bubble"><p>{chat.answer}</p></div>
              </div>
            ))}
          </div>

          {!imageUploaded && (
            <div className="initial-ui">
              <div className="upload-box">
                <input type="file" onChange={handleImageUpload} className="file-input" />
                <p className="upload-text">ADD YOUR IMAGE HERE</p>
                <span className="plus-icon">+</span>
              </div>
              <input type="text" placeholder="Ask your first question!" value={question} onChange={handleQuestionChange} className="question-input" />
              <button onClick={handleFirstSubmit} disabled={loading} className="submit-button">
                {loading ? 'Processing...' : 'Ask me Anything!'}
              </button>
            </div>
          )}

          {imageUploaded && (
            <div className="input-section">
              <input type="text" placeholder="Ask another question!" value={question} onChange={handleQuestionChange} className="question-input" />
              <button onClick={handleFollowUp} disabled={loading} className="submit-button">
                {loading ? 'Processing...' : 'Ask'}
              </button>
            </div>
          )}

          {conversation.length > 0 && (
            <button onClick={handleReset} disabled={loading} className="reset-button">
              {loading ? 'Resetting...' : 'Reset'}
            </button>
          )}

          {error && <p className="error">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
