import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [imageUploaded, setImageUploaded] = useState(false);

  // Handle image upload and preview
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // Upload the image to the server
  const uploadImage = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/upload_image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      if (response.data.message) {
        setImageUploaded(true); // Set state indicating image is uploaded
      }
    } catch (error) {
      setError('Failed to upload the image. Please try again.');
      console.error('Error uploading the image:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle question input change
  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  // Submit question to the server
  const handleSubmitQuestion = async (event) => {
    event.preventDefault();
    if (!imageUploaded) {
      alert('Please upload an image before asking a question.');
      return;
    }
    if (!question) {
      alert('Please enter a question.');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask_question', { question }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      setAnswer(response.data.answer);
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <div>
            <h4>
              "Where vision meets conversation— seamlessly recognizing and discussing 
              your images."
            </h4>
          </div>
        </div>
      </div>
      <div className="main-content">
        {!imageUploaded ? (
          <div className="initial-ui">
            <h1>Hello,</h1>
            <p>How can I help you today?</p>
            <div className="upload-placeholder">
              {imagePreviewUrl ? (
                <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-preview" />
              ) : (
                <div className="upload-box">
                  <input type="file" onChange={handleImageUpload} />
                  <p>Add your image here</p>
                </div>
              )}
            </div>
            <button onClick={uploadImage} disabled={loading || !image}>
              {loading ? 'Uploading...' : 'Upload Image'}
            </button>
          </div>
        ) : (
          <div className="result-ui">
            <div className="chat-interface">
              <h2>
                <FaRobot className="robot-icon" /> Vision Dialog
              </h2>
              <p className="online-status">• Online</p>
              {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-result" />}
              <div className="chat-container">
                {answer && (
                  <>
                    <div className="chat-bubble user-bubble">
                      <p>{question}</p>
                    </div>
                    <div className="chat-bubble bot-bubble">
                      <p>{answer}</p>
                    </div>
                  </>
                )}
              </div>
              <input
                type="text"
                placeholder="Ask me anything!"
                value={question}
                onChange={handleQuestionChange}
              />
              <button onClick={handleSubmitQuestion} disabled={loading}>
                {loading ? 'Processing...' : 'Ask me Anything!'}
              </button>
            </div>
          </div>
        )}
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
