import React, { useState } from 'react';
import axios from 'axios';
import './app.css';
import RobotImage from './logo.png'; // Update with the correct path

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [stage, setStage] = useState(1); // 1: Initial, 2: After image and question submission

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) {
      alert('Please provide both an image and a question.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/recognize_and_answer', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnswer(response.data.answer);
      setStage(2); // Update to the next stage after getting the answer
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
        </div>
      </div>
      <div className="main-content">
        {stage === 1 ? (
          <div className="initial-ui">
            <h1>Hello,</h1>
            <p>How can I help you today?</p>
            <div className="upload-placeholder">
              <input type="file" onChange={handleImageUpload} />
              <p>Add your image here</p>
            </div>
            <input
              type="text"
              placeholder="Ask me anything!"
              value={question}
              onChange={handleQuestionChange}
            />
            <button onClick={handleSubmit} disabled={loading}>
              {loading ? 'Processing...' : 'Ask me Anything!'}
            </button>
          </div>
        ) : (
          <div className="result-ui">
            <div className="chat-interface">
              <h2>VisionDialog</h2>
              <p className="online-status">• Online</p>
              {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image" />}
              <div className="question">
                <p>{`Hello VisionDialog, ${question}`}</p>
              </div>
              <div className="answer">
                <p>{answer}</p>
              </div>
            </div>
          </div>
        )}
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
