import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [conversationStarted, setConversationStarted] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError('');

    const formData = new FormData();

    // If the conversation hasn't started, include the image in the request
    if (!conversationStarted) {
      if (!image) {
        setError('Please upload an image.');
        setLoading(false);
        return;
      }
      formData.append('image', image);
    }

    if (!question) {
      setError('Please provide a question.');
      setLoading(false);
      return;
    }

    formData.append('question', question);

    try {
      const response = await axios.post('http://localhost:5000/ask', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setAnswer(response.data.answer);
      setConversationStarted(true); // Mark the conversation as started
      setQuestion(''); // Clear the question input for the next question
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEndConversation = async () => {
    try {
      await axios.post('http://localhost:5000/end_conversation');
      setConversationStarted(false);
      setImage(null);
      setImagePreviewUrl(null);
      setAnswer('');
      setQuestion('');
    } catch (error) {
      setError('Failed to end the conversation. Please try again.');
      console.error('Error ending the conversation:', error);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <h4>"Where vision meets conversation."</h4>
        </div>
      </div>
      <div className="main-content">
        {!conversationStarted ? (
          <div className="initial-ui">
            <h1>Hello,</h1>
            <p>How can I help you today?</p>
            <div className="upload-placeholder">
              {imagePreviewUrl ? (
                <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-preview" />
              ) : (
                <div className="upload-box">
                  <input type="file" onChange={handleImageUpload} />
                  <p>Add your image here</p>
                </div>
              )}
            </div>
            <input
              type="text"
              placeholder="Ask your first question!"
              value={question}
              onChange={handleQuestionChange}
            />
            <button onClick={handleSubmit} disabled={loading}>
              {loading ? 'Processing...' : 'Ask'}
            </button>
          </div>
        ) : (
          <div className="chat-interface">
            <h2>
              <FaRobot className="robot-icon" /> Vision Dialog
            </h2>
            <p className="online-status">â€¢ Online</p>
            {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-result" />}
            <div className="chat-container">
              <div className="chat-bubble user-bubble">
                <p>{question}</p>
              </div>
              <div className="chat-bubble bot-bubble">
                <p>{answer}</p>
              </div>
            </div>
            <input
              type="text"
              placeholder="Ask another question..."
              value={question}
              onChange={handleQuestionChange}
            />
            <button onClick={handleSubmit} disabled={loading}>
              {loading ? 'Processing...' : 'Ask'}
            </button>
            <button onClick={handleEndConversation} className="end-button">
              End Conversation
            </button>
          </div>
        )}
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
