import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [stage, setStage] = useState(1); // 1: Initial, 2: After image upload, 3: After question submission

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    setImage(file);

    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }

    // Upload image to the server
    const formData = new FormData();
    formData.append('image', file);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/upload_image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      if (response.data.status === 'Image uploaded and encoded successfully') {
        setStage(2); // Proceed to question input stage
      }
    } catch (error) {
      setError('Failed to upload image. Please try again.');
      console.error('Error uploading image:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!question) {
      alert('Please provide a question.');
      return;
    }

    const requestPayload = { question };

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask_question', requestPayload, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      setAnswer(response.data.answer);
      setStage(3); // Update to the chat view after getting the answer
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <h4>"Where vision meets conversation—seamlessly recognizing and discussing your images."</h4>
        </div>
      </div>
      <div className="main-content">
        {stage === 1 ? (
          <div className="initial-ui">
            <h1>Hello,</h1>
            <p>How can I help you today?</p>
            <div className="upload-placeholder">
              {imagePreviewUrl ? (
                <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-preview" />
              ) : (
                <div className="upload-box">
                  <input type="file" onChange={handleImageUpload} />
                  <p>Add your image here</p>
                </div>
              )}
            </div>
          </div>
        ) : stage === 2 ? (
          <div className="question-input-ui">
            <h2>Ask a question</h2>
            <input
              type="text"
              placeholder="Ask me anything!"
              value={question}
              onChange={handleQuestionChange}
            />
            <button onClick={handleSubmit} disabled={loading}>
              {loading ? 'Processing...' : 'Ask me Anything!'}
            </button>
          </div>
        ) : (
          <div className="result-ui">
            <div className="chat-interface">
              <h2><FaRobot className="robot-icon" /> Vision Dialog</h2>
              <p className="online-status">• Online</p>
              {imagePreviewUrl && <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-result" />}
              <div className="chat-container">
                <div className="chat-bubble user-bubble">
                  <p>{question}</p>
                </div>
                <div className="chat-bubble bot-bubble">
                  <p>{answer}</p>
                </div>
              </div>
            </div>
          </div>
        )}
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
