import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot, FaUser } from 'react-icons/fa'; 
import './app.css';

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [conversation, setConversation] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [imageUploaded, setImageUploaded] = useState(false);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
      setImageUploaded(true);
    }
  };

  const handleFirstSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) {
      alert('Please provide both an image and a question.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/upload_image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setConversation([...conversation, { question, answer: response.data.answer }]);
      setQuestion('');
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!question) {
      alert('Please enter a question.');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask_question', { question });
      setConversation([...conversation, { question, answer: response.data.answer }]);
      setQuestion('');
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <h1 className="header">VisionDialog</h1>
        <p className="status">• Online</p>
      </div>
      <div className="main-content">
        {imagePreviewUrl && (
          <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image" />
        )}

        <div className="chat-container">
          {conversation.map((chat, index) => (
            <div key={index} className="chat-bubble">
              <p className="chat-message">{chat.answer}</p>
            </div>
          ))}
        </div>

        {!imageUploaded ? (
          <div className="upload-section">
            <input type="file" onChange={handleImageUpload} className="file-input" />
            <p className="upload-instruction">ADD YOUR IMAGE HERE</p>
          </div>
        ) : (
          <div className="input-section">
            <input
              type="text"
              placeholder="describe me about the image"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              className="question-input"
            />
            <button onClick={handleFirstSubmit} disabled={loading} className="submit-button">
              {loading ? 'Processing...' : 'Ask'}
            </button>
          </div>
        )}

        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
