import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleImageUpload = (event) => {
    setImage(event.target.files[0]);
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) {
      alert("Please provide both an image and a question.");
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/recognize_and_answer', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnswer(response.data.answer);
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div className="container">
        <h1 className="title">Image Recognition Chatbot</h1>
        <form onSubmit={handleSubmit} className="form">
          <input type="file" onChange={handleImageUpload} className="file-input" />
          <input 
            type="text" 
            value={question} 
            onChange={handleQuestionChange} 
            placeholder="Ask a question about the image..." 
            className="text-input" 
          />
          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? 'Processing...' : 'Submit'}
          </button>
        </form>
        {error && <p className="error">{error}</p>}
        {answer && <div className="result"><h2>Answer:</h2><p>{answer}</p></div>}
      </div>
    </div>
  );
};

export default App;
