import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot, FaUser } from 'react-icons/fa'; 
import './app.css';
import RobotImage from './logo.png'; 

const App = () => {
  const [image, setImage] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  const [question, setQuestion] = useState('');
  const [conversation, setConversation] = useState([]); // Store the conversation
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [imageUploaded, setImageUploaded] = useState(false); // Flag to check if image is uploaded

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setImage(file);

    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreviewUrl(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleFirstSubmit = async (event) => {
    event.preventDefault();
    if (!image || !question) {
      alert('Please provide both an image and a question.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('question', question);

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/upload_image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setConversation([...conversation, { question, answer: response.data.answer }]); // Add the first question and answer
      setImageUploaded(true); // Mark the image as uploaded
      setQuestion(''); // Reset question input
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!question) {
      alert('Please enter a question.');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/ask_question', { question });
      setConversation([...conversation, { question, answer: response.data.answer }]); // Add question and answer to conversation
      setQuestion(''); // Reset question input
    } catch (error) {
      setError('Failed to get the answer. Please try again.');
      console.error('Error fetching the answer:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <img src={RobotImage} alt="Robot" className="robot-image" />
        <div className="logo">
          <h1>Vision Dialog</h1>
          <h4>"Where vision meets conversation—seamlessly recognizing and discussing your images."</h4>
        </div>
      </div>
      <div className="main-content">
        <div className="chat-interface">
          <h2><FaRobot className="robot-icon" /> Vision Dialog</h2>
          <p className="online-status">• Online</p>

          {imagePreviewUrl && !imageUploaded && (
            <img src={imagePreviewUrl} alt="Uploaded" className="uploaded-image-preview" />
          )}

          {!imageUploaded ? (
            <div className="initial-ui">
              <div className="upload-box">
                <input type="file" onChange={handleImageUpload} />
                <p>Add your image here</p>
              </div>
              <input
                type="text"
                placeholder="Ask your first question!"
                value={question}
                onChange={handleQuestionChange}
              />
              <button onClick={handleFirstSubmit} disabled={loading}>
                {loading ? 'Processing...' : 'Submit'}
              </button>
            </div>
          ) : (
            <div className="chat-container">
              {conversation.map((chat, index) => (
                <div key={index}>
                  <div className="chat-bubble user-bubble">
                    <FaUser className="user-icon" />
                    <p>{chat.question}</p>
                  </div>
                  <div className="chat-bubble bot-bubble">
                    <FaRobot className="robot-icon" />
                    <p>{chat.answer}</p>
                  </div>
                </div>
              ))}
              <div className="input-section">
                <input
                  type="text"
                  placeholder="Ask another question!"
                  value={question}
                  onChange={handleQuestionChange}
                />
                <button onClick={handleSubmit} disabled={loading}>
                  {loading ? 'Processing...' : 'Ask'}
                </button>
              </div>
            </div>
          )}

          {error && <p className="error">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
